import pandas as pdclass MetaData:    meta_columns = ['ID',                    'County',                    'State_PM',                    'Abs_PM',                    'Latitude',                    'Longitude',                    'Lanes',                    'Name']    def __init__(self, meta):        self.meta_file = meta        self.df = None        # Read in metatable        self.read_meta()    def read_meta(self):        self.df = (pd.read_csv(self.meta_file, sep='\t', usecols=self.meta_columns, engine='python')                   .rename(str.lower, axis='columns')                   .set_index('id'))